        public async Task<IActionResult> CreateTopics()
        {
            if (_commonResource.GetTopics().Count() == 0)
            {
                var topics = new List<TopicModel> {
                    //Populate topics
                new TopicModel
                {
                    Name = "Emotional well-being",
                    Description = "The implications of decreased emotional well-being are related to mental health concerns such as stress, depression, and anxiety. These in turn can contribute to physical ill-health such as digestive disorders, sleep disturbances, and general lack of energy.[1] The profile of a person prone to emotional distress is usually someone with low self-esteem, pessimistic, emotionally sensitive, very self-criticalâ€¦, people who need to constantly assert themselves through their behavior. "
                },
                new TopicModel
                {
                    Name = "Bullying & abuse",
                    Description = "Bullying is the use of force, threat, or coercion to abuse, intimidate or aggressively dominate others. The behavior is often repeated and habitual. One essential prerequisite is the perception, by the bully or by others, of an imbalance of social or physical power, which distinguishes bullying from conflict."
                },
                 new TopicModel
                {
                    Name = "School & work",
                    Description = "Daily issues related to school and work."
                },
                    new TopicModel
                {
                    Name = "Sex & relationships",
                    Description = "Sex & relationships related issues"
                }, new TopicModel
                {
                    Name = "Friends & family",
                    Description = "Family and friends related issues"
                },new TopicModel
                {
                    Name = "Identity",
                    Description = "Identity related issues"
                }, new TopicModel
                {
                    Name = "Physical health",
                    Description = "Physical health related issues"
                }
                };


                foreach (var item in topics)
                {
                    _commonResource.AddTopics(item);
                }
                await _commonResource.SaveTopicAsync();
                return Ok("Data Newly Populated");
            }
            return Ok("Already populated");
        }
